    vendDocumentTableType = this.type(_purchTable);
    if (vendDocumentTableType)
    {
        vendDocumentTableType.defaultField(_fieldId);
    }
}

/// <summary>
/// Applies default values to all fields.
/// </summary>
/// <param name="_purchParmTable">
/// An instance of the <c>PurchParmTable</c> Table, used to track state; optional.
/// </param>
/// <param name="_purchTable">
/// A <c>PurchTable</c> table record, used to copy values from; optional.
/// </param>
/// <param name="_ledgerJournalTrans">
/// Not used in this context.
/// </param>
/// <param name="_resetFieldState">
/// Not used in this context.
/// </param>
public void defaultRow(PurchTable _purchTable = null, LedgerJournalTrans _ledgerJournalTrans = null, boolean _resetFieldState = false)
{
    VendDocumentTableType vendDocumentTableType = this.type(_purchTable);

    if (vendDocumentTableType)
    {
        // If a TransDate was already passed in, then ensure we carry it forward to vendDocumentTableType
        if (this.TransDate)
        {
            vendDocumentTableType.vendDocumentTableMap().TransDate = this.TransDate;
        }

        vendDocumentTableType.defaultRow();
    }
}

