{
    if (this.ParmJobStatus != ParmJobStatus::Executed)
    {
        OfficialsServ_RU::deleteRelationTrans(this.OffSessionId_RU, this.OffReportType_RU);
        if (this.Ordering == DocumentStatus::Facture_RU)
        {
            OfficialsServ_RU::deleteRelationTrans(this.OffSessionId_RU,
                                                    this.OffReportType_RU == OffReportType_RU::PurchInvoice ?
                                                    OffReportType_RU::VendFacture                           :
                                                    OffReportType_RU::VendFactureCreditNote);
        }
    }
}

