/// <summary>
/// Displays the label text for the current value in the ordering field.
/// </summary>
/// <returns>
/// A label text for the current document status.
/// </returns>
display PurchOrderingDisplay displayOrdering()
{
    SysDictEnum                 dictEnum;
    PurchDocumentStatusDisplay  documentStatusTxt;

    if (this.Ordering == DocumentStatus::PackingSlip)
    {
        documentStatusTxt = "@SYS333836";
    }
    else if (this.Ordering == DocumentStatus::ConfirmationRequest &&
             VendTable::find(this.OrderAccount).VendVendorCollaborationType != VendVendorCollaborationType::Disabled)
    {
        documentStatusTxt = "@SCM:PurchaseOrderBindingConfirmationRequest";
    }
    else
    {
        dictEnum = SysDictEnum::newName(enumStr(DocumentStatus));
        documentStatusTxt = dictEnum.value2Label(enum2int(this.Ordering));
    }

    return documentStatusTxt;
}

