/// <summary>
/// Gets the receive quantity.
/// </summary>
/// <returns>
/// Returns
/// </returns>
display CustomsQty_IN getReceive_IN()
{
    CustomsInvoiceRegnJournalLine_IN    customsInvoiceRegnJournalLine;
    CustomsInvoiceRegnJournalTable_IN   customsInvoiceRegnJournalTable;
    CustomsInvoiceRegnTrans_IN          customsInvoiceRegnTrans;

    select sum(Receive) from customsInvoiceRegnTrans
        join RecId from customsInvoiceRegnJournalLine
            where customsInvoiceRegnTrans.CustomsInvoiceRegnJournalLineRecId == customsInvoiceRegnJournalLine.RecId
        join OrderId, CustomsImportInvoiceNumberTable, RecId from customsInvoiceRegnJournalTable
        where customsInvoiceRegnJournalLine.CustomsInvoiceRegnJournalTableRecId == customsInvoiceRegnJournalTable.RecId
           && customsInvoiceRegnJournalTable.OrderId == this.PurchId
           && customsInvoiceRegnJournalTable.CustomsImportInvoiceNumberTable == this.CustomsImportInvoiceNumberTable_IN;

    return customsInvoiceRegnTrans.Receive;
}

/// <summary>
/// Gets the remaining quantity.
/// </summary>
/// <returns>
/// Returns the remaining quantity.
/// </returns>
display CustomsQty_IN getRemainingQuantity_IN()
{
    return(this.getReceive_IN() - this.getBillOfEntryQuantity_IN());
}

