/// <summary>
/// Initializes the packing slip instance from a load line.
/// </summary>
/// <param name = "_loadLine">A load line.</param>
/// <param name = "_maxUsedPackingSlipIdNum">A maximum positive integer that was used in PackingSlipId with the same load ID.</param>
/// <param name = "_parmId">A parameter ID.</param>
public void initPackingSlipPurchParmFromLoadLine(WHSLoadLine _loadLine, int _maxUsedPackingSlipIdNum, ParmId _parmId)
{
    WHSParameters whsParameters = WHSParameters::find();
    WHSShipmentTable shipmentTable = WHSShipmentTable::find(_loadLine.ShipmentId);

    switch (whsParameters.CaptureReceivingCompletedPackingSlip)
    {
        case WHSCaptureReceivingCompletedPackingSlip::Optional:
            if (shipmentTable.ReceivingCompletedPackingSlipId)
            {
                this.assignNumAndDateFromShipmentReceivingCompletedParams(shipmentTable);
            }
            else
            {
                this.DocumentDate = DateTimeUtil::getSystemDate(DateTimeUtil::getClientMachineTimeZone());
                this.assignDefaultNum(_loadLine.LoadId, _maxUsedPackingSlipIdNum);
            }
            break;
        case WHSCaptureReceivingCompletedPackingSlip::Always:
            this.assignNumAndDateFromShipmentReceivingCompletedParams(shipmentTable);
            break;
        default:
            this.assignDefaultNum(_loadLine.LoadId, _maxUsedPackingSlipIdNum);
    }

    this.ParmId           = _parmId;
    this.ParmJobStatus    = ParmJobStatus::Waiting;
    this.TransDate        = DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone());
    this.LoadId           = _loadLine.LoadId;
}

private void assignNumAndDateFromShipmentReceivingCompletedParams(WHSShipmentTable _shipmentTable)
{
    this.DocumentDate = _shipmentTable.ReceivingCompletedDocumentDate;
    this.Num = _shipmentTable.ReceivingCompletedPackingSlipId;
}

private void assignDefaultNum(str _loadId, int _maxUsedPackingSlipIdNum)
{
    this.Num = _loadId + '_' + int2str(_maxUsedPackingSlipIdNum);
}

