public void modifiedField(FieldId _fieldId)
{
    // this method is same as SYS layer
    VendDocumentTableType vendDocumentTableType;

    super(_fieldId);

    switch (_fieldId)
    {
        case fieldNum(PurchParmTable, TransDate):
            vendDocumentTableType = this.type();
            if (vendDocumentTableType)
            {
                this.fieldState(fieldNum(PurchParmTable, CashDiscDate), FieldState::NotSet);
                this.fieldState(fieldNum(PurchParmTable, FixedDueDate), FieldState::NotSet);
                vendDocumentTableType.defaultField(fieldNum(PurchParmTable, CashDiscDate));
                vendDocumentTableType.defaultField(fieldNum(PurchParmTable, FixedDueDate));
            }

            if (!this.FixedExchRate)
            {
                if (!this.useDocumentDate())
                {
                    this.initFromExchRates();
                }
            }
            break;

        case fieldNum(PurchParmTable, DocumentDate):
            vendDocumentTableType = this.type();
            if (vendDocumentTableType)
            {
                this.fieldState(fieldNum(PurchParmTable, CashDiscDate), FieldState::NotSet);
                this.fieldState(fieldNum(PurchParmTable, FixedDueDate), FieldState::NotSet);
                vendDocumentTableType.defaultField(fieldNum(PurchParmTable, CashDiscDate));
                vendDocumentTableType.defaultField(fieldNum(PurchParmTable, FixedDueDate));
            }

            if (!this.FixedExchRate)
            {
                if (this.useDocumentDate())
                {
                    this.initFromExchRates();
                }
            }

            break;

        case fieldNum(PurchParmTable, PaymMode):
            this.paymModeModified();
            break;

        case fieldNum(PurchParmTable, PaymentSched):
            vendDocumentTableType = this.type();
            if (vendDocumentTableType)
            {
                this.fieldState(fieldNum(PurchParmTable, FixedDueDate), FieldState::NotSet);
                vendDocumentTableType.defaultField(fieldNum(PurchParmTable, FixedDueDate));
            }
            break;

        case fieldNum(PurchParmTable, Payment):
            if (this.Payment != '')
            {
                this.PaymentSched = PaymTerm::find(this.Payment).PaymSched;
            }

            vendDocumentTableType = this.type();
            if (vendDocumentTableType)
            {
                this.fieldState(fieldNum(PurchParmTable, FixedDueDate), FieldState::NotSet);
                vendDocumentTableType.defaultField(fieldNum(PurchParmTable, FixedDueDate));
            }
            break;

        case fieldNum(PurchParmTable, CashDiscCode):
            vendDocumentTableType = this.type();
            if (vendDocumentTableType)
            {
                this.fieldState(fieldNum(PurchParmTable, CashDiscPercent), FieldState::NotSet);
                vendDocumentTableType.defaultField(fieldNum(PurchParmTable, CashDiscPercent));
            }
            break;

        case fieldNum(PurchParmTable, CurrencyCode):
            if (!this.FixedExchRate)
            {
                this.initFromExchRates();
            }
            break;

        case fieldNum(PurchParmTable, FixedExchRate):
            this.initFromExchRates();
            break;

        case fieldNum(PurchParmTable, euroTriangulation):
            this.initFromExchRates(false);
            break;

        case fieldNum(PurchParmTable, PurchId):
            this.BankLCImportLine = 0;
            if (this.Ordering == DocumentStatus::BillOfEntry_IN)
            {
                if (this.CustomsImportInvoiceNumberTable_IN)
                {
                    // refresh record
                    PurchParmLine::deleteFromPurchParmLine_IN(this.ParmId, this.TableRefId);
                }
                this.CustomsImportInvoiceNumberTable_IN = 0;
                this.CustomsBillOfEntryNumberTable_IN = 0;
            }
            break;

        default: break;
    }
}

