/// <summary>
/// Determines whether the <paramref name="_fieldIdToCheck" /> field is valid.
/// </summary>
/// <param name="field">
/// The field ID of the field to validate.
/// </param>
/// <returns>
/// true if the field is valid; otherwise, false.
/// </returns>
public boolean validateField(FieldId field)
{
    boolean                 isValid = true;
    VendDocumentTableType   vendDocumentTableType;

    vendDocumentTableType = this.type();
    if (vendDocumentTableType != null)
    {
        isValid = vendDocumentTableType.validateField(field);
    }

    if (field == fieldNum(PurchParmTable, Num))
    {
        if (strContains(this.Num, "\t"))
        {
            return checkfailed(strFmt("@SCM:ValidPackslipId"));
        }

        str specialChars = '<>!=';

        if (strFind(this.Num, specialChars, 1, 1))
        {
            return checkfailed("@SCM:PackingSlipIdWrongFirstSymbolWarning");
        }
    }

    if (isValid)
    {
        isValid = super(field);
    }

    return isValid;
}

/// <summary>
///     Determines whether the parameter "_fieldName" field is valid.
/// </summary>
/// <param name="_fieldName">
///     The field name of the field to validate.
/// </param>
/// <param name="_arrayIndex">
///     The array index of the field to validate.
/// </param>
/// <returns>
///     true if the field is valid; otherwise, false.
/// </returns>
public boolean validateFieldValue(
    FieldName _fieldName,
    int _arrayIndex = 1)
{
    boolean ret = super(_fieldName, _arrayIndex);
    // <GIN>
    boolean custParameterMarked = TaxParameters::checkParameterForPosting_IN(TaxType_IN::Customs);

    switch (fieldName2id(tableNum(PurchParmTable), _fieldName))
    {
        case fieldNum(PurchParmTable, CustomsBillOfEntryNumberTable_IN):
            if (custParameterMarked && this.CustomsBillOfEntryNumberTable_IN)
            {
                if (!CustomsBillOfEntryNumberTable_IN::checkBillOfEntryNumber(CustomsBillOfEntryNumberTable_IN::find(this.CustomsBillOfEntryNumberTable_IN).BillOfEntryNumber))
                {
                    ret = checkFailed(strFmt("@GLS5649",this.CustomsBillOfEntryNumberTable_IN));
                }
            }
            break;

        case fieldNum(PurchParmTable, CustomsImportInvoiceNumberTable_IN):
            if (custParameterMarked && this.CustomsImportInvoiceNumberTable_IN)
            {
                if (!CustomsImportInvoiceNumberTable_IN::checkImporterInvoiceNumber(CustomsImportInvoiceNumberTable_IN::find(this.CustomsImportInvoiceNumberTable_IN).ImporterInvoiceNumber))
                {
                    ret = checkFailed(strFmt("@GLS5745",this.CustomsImportInvoiceNumberTable_IN));
                }
            }
            break;
    }
    // </GIN>
    return ret;
}

public boolean validateWrite()
{
    Boolean isValid = this.validateWrite_ServerPre();

    if (isValid)
    {
        isValid = super();
    }

    return isValid;
}

/// <summary>
/// Determines whether the current record is valid and ready to be written to the database.
/// </summary>
/// <returns>
/// true if the record is valid; otherwise, false.
/// </returns>
/// <remarks>
/// This method was created to move execution of the logic for <c>validateWrite</c> onto the server.
/// The logic is before other validation logic because the kernel validates information that will not
/// be exposed as faults.
/// </remarks>
private boolean validateWrite_ServerPre()
{
    boolean                 isValid;
    VendDocumentTableType    vendDocumentTableType;

    isValid = true;

    vendDocumentTableType = this.type();
    if (vendDocumentTableType != null)
    {
        isValid = vendDocumentTableType.validateRow();
    }

    return isValid;
}

