/// <summary>
/// Checks duplicate BOE number.
/// </summary>
/// <param name="_customsBillOfEntryNumber">
/// The customs bill of entry number.
/// </param>
/// <param name="_customsImporterInvoiceNumber_IN">
/// The customs importer invoice number.
/// </param>
/// <param name="_purchId">
/// The purch id.
/// </param>
/// <returns>
/// Returns true if the duplicate BOE number is valid.
/// </returns>
public static boolean  checkDuplicateBOENumber_IN(
    CustomsBillOfEntryNumber_IN     _customsBillOfEntryNumber,
    CustomsImporterInvoiceNumber_IN _customsImporterInvoiceNumber_IN,
    PurchIdBase                     _purchId)
{
    if (_customsImporterInvoiceNumber_IN
        && _purchId)
    {
        return (select firstonly RecId from purchParmTable
                    where   purchParmTable.PurchId                              == _purchId
                        &&  purchParmTable.CustomsBillOfEntryNumberTable_IN     == CustomsBillOfEntryNumberTable_IN::findByCustomsBillOfEntryNumber(_customsBillOfEntryNumber).RecId
                        &&  purchParmTable.CustomsImportInvoiceNumberTable_IN   == CustomsImportInvoiceNumberTable_IN::findByImporterInvoiceNumber(_customsImporterInvoiceNumber_IN).RecId).RecId !=0;
    }

    return false;
}

/// <summary>
/// Checks duplicate importer number.
/// </summary>
/// <param name="_customsImporterInvoiceNumberTableRecId">
/// The customs importer invoice number table RecId.
/// </param>
/// <param name="_purchId">
/// The purch id.
/// </param>
/// <param name="_parmId">
/// The parm id.
/// </param>
/// <returns>
/// Returns true if the duplicate importer number is valid.
/// </returns>
public static boolean  checkDuplicateImporterNumber_IN(
    RefRecId        _customsImporterInvoiceNumberTableRecId,
    PurchIdBase     _purchId,
    ParmId          _parmId)

{
    if (_customsImporterInvoiceNumberTableRecId && _purchId)
    {
        return (select firstonly RecId from purchParmTable
                    where purchParmTable.PurchId                            == _purchId
                       && purchParmTable.CustomsImportInvoiceNumberTable_IN == _customsImporterInvoiceNumberTableRecId
                       && purchParmTable.ParmId                             == _parmId).RecId !=0;
    }
    return false;
}

