}

static VendExchRate defaultExchRate()
{
    return VendDocumentTableMap::defaultExchRate();
}

static ExchrateSecondary defaultExchRateSecondary()
{
    return VendDocumentTableMap::defaultExchRateSecondary();
}

static void deleteWithoutUpdate(ParmId _parmId, boolean _deletePending = true)
{
    PurchParmTable  purchParmTable;

    ttsbegin;

    // <GEEU>
    #EECountryRegionCodes
    if (SysCountryRegionCode::isLegalEntityInCountryRegion(#easternEuropeAllandRU))
    {
        OfficialsServ_RU::deleteRelationTransParm(_parmId, _deletePending);
    }
    // </GEEU>

    if (_deletePending)
    {
        delete_from purchParmTable
            where purchParmTable.ParmId == _parmId;
    }
    else
    {
        delete_from purchParmTable
            where purchParmTable.ParmId == _parmId
               && purchParmTable.VendInvoiceSaveStatus != VendInvoiceSaveStatus::Pending;
    }

    ttscommit;
}

static boolean exist(ParmId         parmId,
                     TradeLineRefId tableRefId)
{
    return (select firstonly RecId from purchParmTable
                where purchParmTable.ParmId     == parmId &&
                      purchParmTable.TableRefId == tableRefId
           ).RecId != 0;
}

/// <summary>
/// Indicates whether a specified purchase order is selected for any kind of posting.
/// </summary>
/// <param name="_purchId">
/// The purchase order ID to check.
/// </param>
/// <returns>
/// true if the specified purchase order is selected for posting; otherwise, false.
/// </returns>
static boolean existsActivePurchId(PurchId _purchId)
{
    PurchParmTable      purchParmTable;
    PurchParmSubTable   purchParmSubTable;

    select firstonly RecId from purchParmTable
        where  purchParmTable.ParmJobStatus == ParmJobStatus::Waiting
    exists join purchParmSubTable
        where  purchParmSubTable.OrigPurchId == _purchId &&
               purchParmSubTable.ParmId      == purchParmTable.ParmId &&
               purchParmSubTable.TableRefId  == purchParmTable.TableRefId;

    return (purchParmTable.RecId != 0);
}

static PurchParmTable  find(ParmId            parmId,
                            TradeLineRefId    tableRefId,
                            boolean          _forUpdate = false)
{
    PurchParmTable  purchParmTable;

    if (parmId  &&  tableRefId)
    {
        if (_forUpdate)
            purchParmTable.selectForUpdate(_forUpdate);

        select firstonly purchParmTable
            where purchParmTable.ParmId     == parmId
               && purchParmTable.TableRefId == tableRefId;
    }

    return purchParmTable;
}

static PurchParmTable findPurchId(PurchId    purchId,
                           ParmId     parmId,
                           boolean    update = false)
{
    PurchParmTable  purchParmTable;

    if (purchId && parmId)
    {
        if (update)
            purchParmTable.selectForUpdate(update);

        select firstonly purchParmTable
            index hint ParmIdPurchIdIdx
            where purchParmTable.PurchId    == purchId  &&
                  purchParmTable.ParmId     == parmId;
    }

    return purchParmTable;
}

static PurchParmTable findRecId(RecId   recId,
                                boolean update = false)
{
    PurchParmTable  purchParmTable;

    if (recId)
    {
        if (update)
            purchParmTable.selectForUpdate(update);

        select firstonly purchParmTable
            index hint recId
            where purchParmTable.RecId == recId;
    }

    return purchParmTable;
}

/// <summary>
/// Sets the <c>PurchParmTable</c> workflow state for the COR: Packing slip posting workflow.
/// </summary>
/// <param name="_purchParmTableRecId">
/// The record ID of the <c>PurchParmTable</c> table.
/// </param>
/// <param name="_purchParmTableWorkflowState">
/// The workflow state enumeration value to be set on the <c>PurchParmTable</c> table.
/// </param>
static void setWorkflowState(RecId                  _purchParmTableRecId,
                             TradeWorkflowState     _purchParmTableWorkflowState)
{
    PurchParmTable purchParmTable;

    ttsbegin;

    purchParmTable                  = PurchParmTable::findRecId(_purchParmTableRecId, true);
    purchParmTable.WorkflowState    = _purchParmTableWorkflowState;
    purchParmTable.doUpdate();

    ttscommit;
}

/// <summary>
/// Determines the number of <c>PurchParmTable</c> records currently being processed.
/// </summary>
/// <returns>
/// The number of current <c>PurchParmTable</c> records.
/// </returns>
public int numberOfJournalsToPost()
{
    PurchParmTable  purchParmTableLocal;

    select count(RecId) from purchParmTableLocal
            where purchParmTableLocal.ParmId            == this.ParmId
                && purchParmTableLocal.PurchId          == this.PurchId
                && purchParmTableLocal.ParmJobStatus    == ParmJobStatus::Waiting;

    return int642int(purchParmTableLocal.RecId);
}

/// <summary>
/// Determines if update to sibling lines is needed within same journal. This is required if multiple <c>PurchParmLine</c> records
/// reference the same <c>PurchLine</c>.
/// </summary>
/// <returns>true if sibling lines exist which must be updated; otherwise false</returns>
public boolean needUpdateSiblingLinesInSameJournal()
{
    PurchParmLine purchParmLineLocal;
    PurchParmLine purchParmLineLocalAnother;

    // <GIN>
    if (TaxParameters::isCustomsEnable_IN()
        && VendDocumentLineMap::isTransactionRelatedToBOE_IN(this.purchTable(), this.purchParmUpdate()))
    {
        return false;
    }
    // </GIN>

    select firstOnly RecId from purchParmLineLocal
        where purchParmLineLocal.ParmId        == this.ParmId
           && purchParmLineLocal.TableRefId    == this.TableRefId
        exists join purchParmLineLocalAnother
            where purchParmLineLocalAnother.RecId           != purchParmLineLocal.RecId
               && purchParmLineLocalAnother.InventTransId   == purchParmLineLocal.InventTransId
               && purchParmLineLocalAnother.ParmId          == this.ParmId
               && purchParmLineLocalAnother.TableRefId      == this.TableRefId;

    return purchParmLineLocal.RecId != 0;
}

/// <summary>
/// Deletes the <c>PurchParmTable</c> table and related tables.
/// </summary>
public void delete()
{
    if (TaxIntegrationUtils::isTransitDocumentEnabled())
    {
        this.TransitDocumentTransactionMap_IN::deleteTransitDocumentsForHeader();
    }

    super();
}

/// <summary>
/// Mark current tax status of <c> PurchParmTable </c> to messy
/// </summary>
private void markCurrentTaxDocumentTaxStatusDirty()
{
    TaxableDocumentDescriptor descriptor = TaxableDocumentDescriptorFactory::getTaxableDocumentDescriptor(this);
    ITaxableDocument taxableDocument = TaxableDocumentObject::construct(descriptor);
    TaxBusinessService::markTaxDocumentTaxStatus(taxableDocument, TaxDocumentCalculationLevel::Dirty);
}

/// <summary>
/// Current buffer of <c> PurchParmTable</c>
/// </summary>
public void update()
{
    super();

    if (TaxSolutionScopeIntegrationUtil::IsCompanyEnabled())
    {
        this.markCurrentTaxDocumentTaxStatusDirty();
    }
}

