Method.Source:
    /// <summary>
    /// Determines if a bid can be unsealed.
    /// </summary>
    /// <returns>
    /// true if the bid can be unsealed; otherwise, false.
    /// </returns>
    public boolean canBidBeUnsealed()
    {
        return (this.BidType == PurchRFQBidType::Sealed
                && this.isSealed == NoYes::Yes
                && this.ExpiryDateTime < DateTimeUtil::utcNow()
				&& this.StatusHigh != PurchRFQStatus::Canceled);
    }

