/// <summary>
/// Determines whether the amendment is editable for a specific <c>PurchRFQCaseTable</c> record.
/// </summary>
/// <returns>
/// true if amendment is editable; otherwise, false.
/// </returns>
public boolean editAmendmentAllowed()
{
    boolean amendmentAllowed;
    boolean isRFQSentOrPublished;

    isRFQSentOrPublished = this.StatusHigh > PurchRFQStatus::Created
                                        || PurchRFQPublishTable_PSN::isRFQCasePublised(this.RecId);

    amendmentAllowed = isRFQSentOrPublished
        && (this.StatusHigh     == PurchRFQStatus::Created
            || this.StatusHigh  == PurchRFQStatus::Sent
            || this.StatusHigh  == PurchRFQStatus::Received
            || this.StatusHigh  == PurchRFQStatus::Rejected
            || this.StatusHigh  == PurchRFQStatus::Declined)
        && this.ExpiryDateTime  >  DateTimeUtil::utcNow();

    return amendmentAllowed;
}

