/// <summary>
/// Updates the order line after currency code conversion.
/// </summary>
/// <param name = "_purchRFQCaseLine">A purchase requests for quote case line</param>
protected void updateLineOnCurrencyCodeConversion(purchRFQCaseLine _purchRFQCaseLine)
{
}

void convertCurrencyCode(CurrencyCode currencyCode, TradeCurencyConversion tradeCurencyConversion = TradeCurencyConversion::ExchRate)
{
    PurchRFQCaseLine    purchRFQCaseLine;
    MarkupTrans         markupTrans;

    ttsbegin;

    while select forupdate purchRFQCaseLine
          where purchRFQCaseLine.rfqCaseId == this.rfqCaseId
    {
        purchRFQCaseLine.convertCurrencyCode(currencyCode, tradeCurencyConversion);
        this.updateLineOnCurrencyCodeConversion(purchRFQCaseLine);
        purchRFQCaseLine.doUpdate();
    }

    while select forupdate markupTrans
          index hint TableRecIdIdx
          where markupTrans.TransTableId  == this.TableId &&
                markupTrans.TransRecId    == this.RecId   &&
               !markupTrans.IsDeleted
    {
        markupTrans.convertCurrencyCode(currencyCode);
        markupTrans.doUpdate();
    }

    VendPaymSched::deleteVendPaymSched(this.TableId, this.RecId);

    this.CurrencyCode = currencyCode;

    ttscommit;
}

void  copyAddressToLine(DialogButton         copyAddress,
                               PurchRFQCaseTable    purchRFQCaseTable)
{
    PurchRFQCaseLine  purchRFQCaseLine;

    if (copyAddress == DialogButton::Cancel)
        return;

    ttsbegin;

    while select forupdate purchRFQCaseLine
        where purchRFQCaseLine.rfqCaseId == this.rfqCaseId &&
           (copyAddress                  == DialogButton::YesToAll ||
            purchRFQCaseLine.DeliveryPostalAddress == purchRFQCaseTable.orig().DeliveryPostalAddress)
    {
        purchRFQCaseLine.setAddressFromPurchRFQCaseTable(this);

        purchRFQCaseLine.write();
    }

    ttscommit;
}

