/// <summary>
/// Copies the expiration date from the RFQ case to all case lines.
/// </summary>
public void copyExpirationDateToLines()
{
    PurchRFQCaseLine purchRFQCaseLine;

    update_recordSet purchRFQCaseLine
        setting
            ExpiryDateTime = this.ExpiryDateTime
        where purchRFQCaseLine.RFQCaseId == this.RFQCaseId;
}

public void delete()
{
    if (appl.ttsLevel() == 0)
    {
        throw error(strFmt("@SCM:TransactionExpected", funcName()));
    }

    PurchRFQCaseTableType purchRFQCaseTableType = this.type();

    purchRFQCaseTableType.deleting();

    PurchRFQCaseLine purchRFQCaseLine;
    PurchReqLine     purchReqLine;

    update_recordset purchReqLine
        setting
            PurchRFQCaseId         = '',
            PurchRFQCaseIdDataArea = ''
        exists join purchRFQCaseLine
        where purchReqLine.LineRefId == purchRFQCaseLine.ReqLineRefId
            && purchRFQCaseLine.RFQCaseId == this.RFQCaseId;

    PurchRFQDeleteDocuRef::deleteDocuRef(this);

    super();

    purchRFQCaseTableType.deleted();
}

