/// <summary>
/// Determines if alternate lines exist for the replies to the current RFQ case.
/// </summary>
/// <returns>
/// true if alternate lines exist; otherwise, false.
/// </returns>
public boolean doAlternatesExist()
{
    PurchRFQTable       purchRFQTable;
    PurchRFQLine        purchRFQLine;

    if (this.RecId && this.StatusHigh > PurchRFQStatus::Sent)
    {
        select firstonly RecId from purchRFQTable
            where purchRFQTable.RFQCaseId == this.RFQCaseId
        exists join purchRFQLine
            where purchRFQLine.RFQId == purchRFQTable.RFQId
                && purchRFQLine.isAlternate == NoYes::Yes;
    }

    return purchRFQTable.RecId != 0;
}

/// <summary>
/// Determines if the expiry dates on the RFQ case lines match the expiry date of the header
/// </summary>
/// <returns>
/// true if the dates match; otherwise, false
/// </returns>
public boolean doLineExpiryDatesMatchHeaderExpiryDate()
{
    PurchRFQCaseLine    purchRFQCaseLine;

    select firstonly RecId from purchRFQCaseLine
        where PurchRFQCaseLine.RFQCaseId == this.RFQCaseId
            && purchRFQCaseLine.ExpiryDateTime != this.ExpiryDateTime;

    return purchRFQCaseLine.RecId == 0;
}

