public void insert()
{
    if (appl.ttsLevel() == 0)
    {
        throw error(strFmt("@SCM:TransactionExpected", funcName()));
    }

    PurchRFQCaseTableType purchRFQCaseTableType = this.type();

    purchRFQCaseTableType.inserting();

    super();

    //Copy the scoring criteria from the default scoring method set on the solicitation type.
    PurchRFQScoringRFQCaseCriteria::copyFromScoringMethod(this.RecId, PurchRFQSolicitationType::find(this.SolicitationType).DefaultScoringMethod);

    purchRFQCaseTableType.inserted();
}

/// <summary>
/// Verifies if the RFQ case is in a state where one of the RFQs can be accepted or rejected.
/// </summary>
/// <returns>
/// A boolean value indicating if the RFQ case can be accepted of rejected.
/// </returns>
public boolean isAcceptRejectAllowed()
{
    boolean isRFQAmendmentEnabled =  PurchRFQParameters::isRFQAmendmentEnabled();

    return ((isRFQAmendmentEnabled    && !this.isAmended) || !isRFQAmendmentEnabled)
        && (this.StatusLow   <  PurchRFQStatus::Rejected)
        && (this.StatusHigh  >  PurchRFQStatus::Sent)
        && this.IsSealed == NoYes::No;
}

