/// <summary>
/// Performs actions that should occur when the expiry date is modified.
/// </summary>
public void modifiedExpiryDate()
{
    if (this.BidType == PurchRFQBidType::Sealed && this.existLine() && !this.doLineExpiryDatesMatchHeaderExpiryDate())
    {
        if (Box::okCancel("@EPR4460200", DialogButton::Yes) == DialogButton::Ok)
        {
            this.copyExpirationDateToLines();
        }
        else
        {
            this.ExpiryDateTime = this.orig().ExpiryDateTime;
        }
    }
}

public void modifiedField(fieldId _fieldId)
{
    super(_fieldId);

    switch (_fieldId)
    {
        case fieldNum(PurchRFQCaseTable, DeliveryPostalAddress):
            LogisticsLocationSelectForm_RFQCaseTable::construct(this).copyAddressToCaller(this.getDeliveryEntityRecord(), this.DeliveryPostalAddress);
            break;

        case fieldNum(PurchRFQCaseTable, InventLocationId):
            this.InventStorageDimMap::modifiedField(fieldNum(InventStorageDimMap, InventLocationId));
            this.setAddressFromInventLocationId(this.InventLocationId);
            if (FormDataUtil::isFormDataSource(this))
            {
                FormDataUtil::getFormDataSource(this).refresh();
            }
            break;

        case fieldNum(PurchRFQCaseTable, InventSiteId):
            this.InventStorageDimMap::modifiedField(fieldNum(InventStorageDimMap, InventSiteId));
            this.setAddressFromInventSiteId(this.InventSiteId);
            if (FormDataUtil::isFormDataSource(this))
            {
                FormDataUtil::getFormDataSource(this).refresh();
            }
            break;

        case fieldNum(PurchRFQCaseTable, rfqType):
            this.resetAgreementPOFields();
            break;

        case fieldNum(PurchRFQCaseTable, ValidityDateStart):
            this.ValidityDateEnd = this.ValidityDateStart;
            break;

        case fieldNum(PurchRFQCaseTable, SolicitationType):
            this.setSolicitationTypeRelatedFields();
            if (FormDataUtil::isFormDataSource(this))
            {
                FormDataUtil::getFormDataSource(this).refresh();
            }
            break;

        case fieldNum(PurchRFQCaseTable, ExpiryDateTime):
            if (this.isSealed == NoYes::Yes)
            {
                this.modifiedExpiryDate();
            }
            break;

        case fieldNum(PurchRFQCaseTable, BidType):
            this.modifiedBidType();

            if (this.BidType == PurchRFQBidType::Sealed
                && this.IsSealed == NoYes::No)
            {
                this.BidType = this.orig().BidType;
            }
            break;
    }
}

private void setSolicitationTypeRelatedFields()
{
    PurchRFQSolicitationType purchRFQSolicitationType = PurchRFQSolicitationType::find(this.SolicitationType);

    if (purchRFQSolicitationType)
    {
        this.RFQCloseVisibilityType = purchRFQSolicitationType.DefaultRFQCloseVisibilityType;
    }
    else
    {
        this.RFQCloseVisibilityType = PurchRFQParameters::find().DefaultRFQCloseVisibilityType;
    }

    this.setDefaultAllowBidByInvitationOnly();
    this.setDefaultBidType();

    PurchRFQScoringRFQCaseCriteria::copyFromScoringMethod(this.RecId, purchRFQSolicitationType.DefaultScoringMethod, true);
}

