/// <summary>
/// Returns a query that selects <c>PurchRFQCaseLine</c> records that are linked to the current
/// <c>PurchRFQCaseTable</c> record.
/// </summary>
/// <param name="_update">
/// A <c>NoYes</c> enumeration value that determines whether the record should be selected for update;
/// optional.
/// </param>
/// <returns>
/// A query that selects <c>PurchRFQCaseLine</c> records.
/// </returns>
Query queryPurchRFQCaseLine(boolean _update = false)
{
    DictRelation    dictRelation;
    DictField       externdictField;
    DictField       dictField;
    Query           query = new Query();
    Counter         c;

    query.addDataSource(tableNum(PurchRFQCaseLine));

    dictRelation = new DictRelation(tableNum(PurchRFQCaseLine));
    dictRelation.loadTableRelation(this.TableId);

    if (dictRelation.lines())
    {
        for (c=1; c <= dictRelation.lines(); c++)
        {
            externdictField   = new DictField(this.TableId,dictRelation.lineExternTableValue(c));
            dictField   = new DictField(tableNum(PurchRFQCaseLine),dictRelation.lineTableValue(c));
            query.dataSourceTable(tableNum(PurchRFQCaseLine)).addRange(dictField.id()).value(queryValue(this.(externdictField.id())));
        }
    }
    query.dataSourceTable(tableNum(PurchRFQCaseLine)).addRange(fieldNum(PurchRFQCaseLine, StatusHigh)).value(strFmt('..%1',PurchRFQStatus::Sent));
    query.dataSourceTable(tableNum(PurchRFQCaseLine)).update(_update);

    return query;
}

