/// <summary>
/// Displays the requester name for a <c>PurchRFQCaseTable</c>.
/// </summary>
/// <returns>
/// Requester name for a <c>PurchRFQCaseTable</c>.
/// </returns>
[SysClientCacheDataMethodAttribute(true)]
public display HcmWorkerName requesterName()
{
    HcmWorkerDetailsView hcmWorkerDetailsView;

    if (this.Requester)
    {
        HcmWorker hcmWorker;

        select Name from hcmWorkerDetailsView
            exists join hcmWorker
            where hcmWorker.Person == hcmWorkerDetailsView.Person
               && hcmWorker.RecId == this.Requester;
    }

    return hcmWorkerDetailsView.Name;
}

display PurchRFQVendNumFilter rfqVendors()
{
    PurchRFQReplyTable  rfqReplyTable;
    PurchRFQTable  rfqTable;

    select count (RecId) from rfqTable
        where rfqTable.rfqCaseId == this.rfqCaseId
            exists join rfqReplyTable
            where rfqTable.rfqId == rfqReplyTable.rfqId;

    int64 numberOfVendors = rfqTable.RecId;

    switch (numberOfVendors)
    {
        case 1:
            return PurchRFQVendNumFilter::Single;
        case 0:
            return PurchRFQVendNumFilter::None;

        default :
            return PurchRFQVendNumFilter::Multiple;
    }
}

boolean setAddressFromInventLocationId(InventLocationId _inventLocationId)
{
    LogisticsLocationRecId          inventLocationAddress;
    InventLocation                  inventLocation;
    boolean                         addressSet;

    addressSet = false;

    if (_inventLocationId)
    {
        inventLocation          = InventLocation::find(_inventLocationId);
        inventLocationAddress   = InventLocation::getLocationFromRole(inventLocation.RecId, LogisticsLocationRoleType::Delivery);

        if (inventLocationAddress)
        {
            this.setLocation(LogisticsLocation::find(inventLocationAddress));
            addressSet = true;
        }
    }

    if (!addressSet)
    {
        addressSet = this.setAddressFromInventSiteId(this.InventSiteId);
    }

    return addressSet;
}

boolean setAddressFromInventSiteId(InventSiteId _inventSiteId)
{
    LogisticsLocationRecId          location;
    InventSite                      inventSite;
    boolean                         addressSet = false;

    if (_inventSiteId)
    {
        inventSite  = InventSite::find(_inventSiteId);
        location   = InventSite::getLocationFromRole(inventSite.RecId, LogisticsLocationRoleType::Delivery);

        if (location)
        {
            this.setLocation(LogisticsLocation::find(location));
            addressSet = true;
        }
    }

    if (!addressSet)
    {
        this.initDefaultAddress();
        addressSet = true;
    }

    return addressSet;
}

