public void update()
{
    if (appl.ttsLevel() == 0)
    {
        throw error(strFmt("@SCM:TransactionExpected", funcName()));
    }

    PurchRFQCaseTable purchRFQCaseTableOrig = this.orig();

    PurchRFQCaseTableType purchRFQCaseTableType = this.type();

    purchRFQCaseTableType.updating();

    if (this.BidType == PurchRFQBidType::Sealed && this.IsSealed == NoYes::No && this.orig().IsSealed == NoYes::Yes)
    {
        this.UnsealedDateTime = DateTimeUtil::getSystemDateTime();
    }

    super();

    if (purchRFQCaseTableOrig.rfqType != this.rfqType)
    {
        PurchRFQCaseLine purchRFQCaseLine;

        while select forupdate purchRFQCaseLine
                where purchRFQCaseLine.rfqCaseId == this.rfqCaseId
        {
            purchRFQCaseLine.rfqType = this.rfqType;
            purchRFQCaseLine.update();
        }
    }

    purchRFQCaseTableType.updated(purchRFQCaseTableOrig);
}

