public boolean validateWrite()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        if ((this.rfqType == PurchRFQType::PurchAgreement) && (this.ValidityDateEnd == dateNull()))
        {
            // The end date is required
            ret = checkFailed("@SYS308987");
        }

        if (ret && this.AllowAlternates == NoYes::No && this.doAlternatesExist())
        {
            ret = checkFailed("@EPR4460288");
        }

        if (ret
            && (this.BidType == PurchRFQBidType::Sealed && this.IsSealed)
            && !PurchRFQSealedBidEncryptionManager::isSealedBidEncryptionPrerequisiteConfigurede())
        {
            ret = checkFailed("@SCM:PurchRFQSealedBidEncryptionPrerequisiteNotCorrectlyConfiguredError");
        }

        if (this.IsVendorQuestionEnabled && this.VendorQuestionCutoffDateTime != DateTimeUtil::minValue() && this.VendorQuestionCutoffDateTime < DateTimeUtil::utcNow())
        {
            ret = checkFailed("@SCM:CutoffDateBeforeCurrentDateErrorMessage");
        }

        if (this.IsVendorQuestionEnabled && this.VendorQuestionCutoffDateTime >= this.ExpiryDateTime)
        {
            ret = checkFailed("@SCM:CutoffDateAfterExpirationDateErrorMessage");
        }
    }

    return ret;
}

static boolean exist(PurchRFQCaseId rfqCaseId)
{
    if (rfqCaseId)
    {
        return (select firstonly RecId from purchRFQCaseTable
                index hint RFQCaseIdx
                where purchRFQCaseTable.rfqCaseId  == rfqCaseId).RecId != 0;
    }

    return false;
}

static PurchRFQCaseTable find(PurchRFQCaseId     rfqCaseId,
                              boolean            _forUpdate = false)

{
    PurchRFQCaseTable  purchRFQCaseTable;

    if (rfqCaseId)
    {
        purchRFQCaseTable.selectForUpdate  (_forUpdate);

        select firstonly purchRFQCaseTable
            index hint RFQCaseIdx
            where purchRFQCaseTable.rfqCaseId == rfqCaseId;
    }

    return purchRFQCaseTable;
}

/// <summary>
/// Finds the <c>PurchRFQCaseTable</c> table buffer on the basis of internal RFQ Id.
/// </summary>
/// <param name="_internalRFQId">
/// The interal RFQ Id.
/// </param>
/// <returns>
/// The buffer of the <c>PurchRFQCaseTable</c> table.
/// </returns>
public static PurchRFQCaseTable findByInternalRFQId(PurchInternalRFQId _internalRFQId)
{
    PurchRFQCaseTable purchRFQCaseTable;
    PurchRFQTable     purchRFQTable;
    VendRFQJour       vendRFQJour;

    select purchRFQCaseTable
        exists join RecId from purchRFQTable
            where purchRFQCaseTable.RFQCaseId == purchRFQTable.RFQCaseId
        exists join RecId from vendRFQJour
            where vendRFQJour.InternalRFQId == _internalRFQId
                && purchRFQCaseTable.RFQCaseId  == vendRFQJour.RFQCaseId;

    return purchRFQCaseTable;
}

public static PurchRFQCaseTable findRecId(recId   _recId, boolean _forUpdate = false)
{
    PurchRFQCaseTable    purchRFQCaseTable;

    if (_recId)
    {
        purchRFQCaseTable.selectForUpdate(_forUpdate);

        select firstonly purchRFQCaseTable
            where purchRFQCaseTable.RecId  == _recId;
    }

    return purchRFQCaseTable;
}

/// <summary>
/// Retrieves the worker <c>recId</c> value for the current user.
/// </summary>
/// <returns>
/// The worker record ID of the current user.
/// </returns>
public static HcmWorkerRecId getCurrentWorker()
{
    return HcmWorker::findByPerson(DirPersonUser::current().PersonParty, false, ConcurrencyModel::Auto).RecId;
}

/// <summary>
/// Gets a list of vendors associated with a RFQ case.
/// </summary>
/// <param name="_rfqCaseId">
/// A RFQ case ID.
/// </param>
/// <returns>
/// A container with a packed list of vendors.
/// </returns>
static public container getVendors(PurchRFQCaseId _rfqCaseId)
{
    PurchRFQVendLink    existingVendorPurchRFQVendLink;
    List                existingVendorsInRFQList;

    existingVendorsInRFQList = new List(Types::String);
    while select VendAccount from existingVendorPurchRFQVendLink
        where existingVendorPurchRFQVendLink.rfqCaseId == _rfqCaseId
    {
        existingVendorsInRFQList.addEnd(existingVendorPurchRFQVendLink.VendAccount);
    }

    return existingVendorsInRFQList.pack();
}

/// <summary>
/// Iterates through the RFQ journals and returns a map of vendor accounts and latest RFQ journal RecId;
/// </summary>
/// <returns>Map of vendor accounts and latest RFQ journal RecId;</returns>
public Map getLatestRFQJournalsForRFQVendors()
{
    PurchRFQVendLink    purchRFQVendLink;
    VendRFQJour         vendRFQJour;
    Map                 vendorLatestJournal = new Map(Types::String, Types::Int64);

    while select VendAccount from purchRFQVendLink
        group by purchRFQVendLink.VendAccount
            where purchRFQVendLink.RFQCaseId == this.RFQCaseId
                join maxof(RecId) from vendRFQJour
                    where VendRFQJour.VendAccount == purchRFQVendLink.VendAccount
                        && vendRFQJour.RFQCaseId == this.RFQCaseId
                        && vendRFQJour.Status == PurchRFQStatus::Sent
    {
        vendorLatestJournal.insert(purchRFQVendLink.VendAccount, vendRFQJour.RecId);
    }

    return vendorLatestJournal;
}

/// <summary>
/// Determines if the AllowBidByInvitationOnly is checked or not.
/// </summary>
/// <param name="_rfqCaseId">
/// The case ID of an RFQ.
/// </param>
/// <returns>
/// true if the AllowBidByInvitationOnly is checked; otherwise, false.
/// </returns>
public static boolean isBiddingByInvite(PurchRFQCaseId  _rfqCaseId)
{
    PurchRFQCaseTable   purchRFQCaseTable;

    if (_rfqCaseId)
    {
        select firstonly AllowBidByInvitationOnly from purchRFQCaseTable
            index hint RFQCaseIdx
            where purchRFQCaseTable.rfqCaseId  == _rfqCaseId;
    }

    return purchRFQCaseTable.AllowBidByInvitationOnly == NoYes::Yes;
}

static container promptConvertCurrencyCode()
{
    Dialog                  dialog;
    DialogField             dialogField;
    DialogButton            dialogButton;

    if (!isConfigurationkeyEnabled(configurationKeyNum(PriceDisc)))
    {
        dialogButton = Box::okCancel("@SYS79467", DialogButton::Ok);
        if (dialogButton == DialogButton::Ok)
            return [NoYes::Yes, TradeCurencyConversion::ExchRate];

        return [NoYes::No, TradeCurencyConversion::ExchRate];
    }

    dialog = new Dialog("@SYS79467");
    dialog.addText("@SYS79467");
    dialogField = dialog.addField(enumStr(TradeCurencyConversion));
    if (dialog.run())
        return [NoYes::Yes, dialogField.value()];

    return [NoYes::No, TradeCurencyConversion::ExchRate];
}

