{
    return Box::yesYesAllNoCancel("@SYS106748", DialogButton::Yes);
}

/// <summary>
/// Determines if the request for quotation is canceled.
/// </summary>
/// <returns>
/// true if the request for quotation is canceled; otherwise, false.
/// </returns>
public boolean isRFQCanceled()
{
    return this.StatusHigh == PurchRFQStatus::Canceled && this.StatusLow == PurchRFQStatus::Canceled;
}

    /// <summary>
    /// Determines if an request for quotation case can be canceled.
    /// </summary>
    /// <returns>
    /// true if the RFQ case can be canceled; otherwise, false.
    /// </returns>
    public boolean canCancelRFQ()
    {
        return this.isAmended == NoYes::No
            && this.StatusHigh > PurchRFQStatus::Created
            && (this.StatusHigh < PurchRFQStatus::Accepted
				|| this.StatusHigh == PurchRFQStatus::Declined);
    }

