/// <summary>
/// If bid type is modified to sealed and expiration date time of any or all the lines is not equal to that of the header then after
/// user's confirmation, the expiration date time is copied from the header to line(s) and
/// <c>IsSealed</c> field is set to yes.
/// </summary>
private void modifiedBidType()
{
    if (this.BidType == PurchRFQBidType::Sealed
        && this.existLine() 
        && !this.doLineExpiryDatesMatchHeaderExpiryDate())
    {
        if (Box::okCancel("@SCM:CopyHeaderExpirationDateTimeToLinesForSealedBidDialog", DialogButton::Ok) == DialogButton::Ok)
        {
            this.copyExpirationDateToLines();
            this.isSealed = NoYes::Yes;
        }
    }
    else if (this.BidType == PurchRFQBidType::Sealed)
    {
        this.isSealed = NoYes::Yes;
    }
    else
    {
        this.isSealed = NoYes::No;
    }
}

/// <summary>
/// Checks if a vendor can send questions related to a RFQ.
/// </summary>
/// <param name = "_rfqcaseId"><c>PurchRFQCaseId</c> field value.</param>
/// <returns>true if the cutoff date is not reached and vendors can still send questions; otherwise, false.</returns>
internal static boolean canVendorSendQuestion(PurchRFQCaseId _rfqcaseId)
{
    // Checks if the cutoff date for vendor questions set by buyer is reached or not.
    return PurchRFQCaseTable::find(_rfqcaseId).VendorQuestionCutoffDateTime > DateTimeUtil::utcNow();
}

/// <summary>
/// Gives the cut-off date and time for vendor questions on a RFQ case Id.
/// </summary>
/// <returns>
/// Vendor questions cut-off date.
/// </returns>
[SysClientCacheDataMethodAttribute(true)]
internal display PurchRFQVendorQuestionCutoffDateTime vendorQuestionCutOffDate()
{
    return PurchRFQCaseTable::find(this.RFQCaseId).VendorQuestionCutoffDateTime;
}
