/// <summary>
/// Updates the order line after currency code conversion.
/// </summary>
/// <param name = "_purchRFQLine">A purchase requests for quote line</param>
protected void updateLineOnCurrencyCodeConversion(purchRFQLine _purchRFQLine)
{
}

void convertCurrencyCode(CurrencyCode currencyCode, TradeCurencyConversion tradeCurencyConversion = TradeCurencyConversion::ExchRate)
{
    PurchRFQLine        purchRFQLine;
    MarkupTrans         markupTrans;

    ttsbegin;

    while select forupdate purchRFQLine
          where purchRFQLine.rfqId == this.rfqId
    {
        purchRFQLine.convertCurrencyCode(currencyCode, tradeCurencyConversion);
        this.updateLineOnCurrencyCodeConversion(purchRFQLine);
        purchRFQLine.doUpdate();
    }

    while select forupdate markupTrans
          index hint TableRecIdIdx
          where markupTrans.TransTableId  == this.TableId &&
                markupTrans.TransRecId    == this.RecId   &&
               !markupTrans.IsDeleted
    {
        markupTrans.convertCurrencyCode(currencyCode);
        markupTrans.doUpdate();
    }

    VendPaymSched::deleteVendPaymSched(this.TableId, this.RecId);

    this.CurrencyCode = currencyCode;

    ttscommit;
}

void  copyAddressToLine(DialogButton         copyAddress,
                               PurchRFQTable        purchRFQTable)
{
    PurchRFQLine  purchRFQLine;

    if (copyAddress == DialogButton::Cancel)
        return;

    ttsbegin;

    while select forupdate purchRFQLine
        where purchRFQLine.rfqId == this.rfqId &&
           (copyAddress                  == DialogButton::YesToAll ||
            purchRFQLine.DeliveryPostalAddress == purchRFQTable.orig().DeliveryPostalAddress)
    {
        purchRFQLine.setAddressFromPurchRFQTable(this);
        // <GBR>
        purchRFQLine.CFOPTable_BR = CFOPValidation_BR::construct(purchRFQLine).getDefaultCFOP();
        // </GBR>
        purchRFQLine.write();
    }

    ttscommit;
}

