private void initAgreementHeader(AgreementHeader _agreementHeader)
{
    RefRecId agreementClassificationRecId = PurchRFQCaseTable::find(this.RFQCaseId).AgreementClassification;

    if (agreementClassificationRecId)
    {
        _agreementHeader.AgreementClassification = agreementClassificationRecId;
    }
    else
    {
        _agreementHeader.AgreementClassification = AgreementClassification::findOrCreateAgreementClassification(tableNum(PurchAgreementHeader)).RecId;
    }

    _agreementHeader.Currency           = this.CurrencyCode;
    _agreementHeader.Language           = this.LanguageId;
    _agreementHeader.Originator         = HcmWorker::find(this.ResponsibleWorkerId).Person;
    _agreementHeader.IsDeleted          = NoYes::No;

    _agreementHeader.DefaultAgreementLineEffectiveDate     = this.ValidityDateStart;
    _agreementHeader.DefaultAgreementLineExpirationDate    = this.ValidityDateEnd;
    _agreementHeader.EarliestLineEffectiveDate             = this.ValidityDateStart;
    _agreementHeader.LatestLineExpirationDate              = this.ValidityDateEnd;
}

