public void initDefaultAddress()
{
    LogisticsPostalAddress postalAddress;
    LogisticsLocationRecId locationRecId;
    LogisticsLocationDefault defaultLocations;
    CompanyInfo companyInfo = CompanyInfo::find();

    defaultLocations = LogisticsLocationDefault::construct(companyInfo);
    locationRecId = defaultLocations.getDefaultLocationRecIdByType(
        LogisticsLocationRole::findBytype(LogisticsLocationRoleType::Delivery));

    if (locationRecId)
    {
        this.setLocation(locationRecId);
    }
    else
    {
        this.DeliveryName           = companyInfo.name();
        this.AddressRefRecId        = companyInfo.RecId;
        this.AddressRefTableId      = companyInfo.TableId;

        postalAddress = DirParty::primaryPostalAddress(companyInfo.RecId);
        if (postalAddress)
        {
            this.DeliveryPostalAddress  = postalAddress.RecId;
            this.DeliveryName = LogisticsLocation::find(postalAddress.Location).Description;
        }
        else
        {
            this.DeliveryPostalAddress  = 0;
        }
    }
}

void initFromContactPerson(ContactPerson _contactPerson = this.contactPerson())
{
    if (this.ContactPerson && _contactPerson.email())
        this.Email = _contactPerson.email();
    else
        this.Email = this.vendTable().email();

    if (this.ContactPerson && _contactPerson.phone())
        this.Phone = _contactPerson.phone();
}

