void  initFromPurchRFQVendLink(PurchRFQVendLink _purchRFQVendLink)
{
    VendTable vendTable = VendTable::find(_purchRFQVendLink.VendAccount);

    this.ContactPerson      = _purchRFQVendLink.ContactPersonId;
    this.Email              = _purchRFQVendLink.Email;
    this.Phone              = _purchRFQVendLink.Phone;
    this.rfqCaseId          = _purchRFQVendLink.rfqCaseId;
    this.VendAccount        = _purchRFQVendLink.VendAccount;
    if (PurchParameters::find().IsSupplierShipmentDatesEnabled)
    {
        this.ShipCalendarId = _purchRFQVendLink.ShipCalendarId;
    }
    this.rfqName            = vendTable.name();
    this.LanguageId         = DirPartyTable::findRec(vendTable.Party).LanguageId;
    this.TaxGroup           = vendTable.TaxGroup;
    this.OverrideSalesTax   = vendTable.OverrideSalesTax;
    this.EnterpriseNumber   = vendTable.getPrimaryRegistrationNumber(TaxRegistrationTypesList::UID);

    vendTable.copyPrimaryRegistrationNumberToVATMap(this);

    // <GBR>
    if (BrazilParameters::isEnabled())
    {
        this.SalesPurchOperationType_BR = _purchRFQVendLink.SalesPurchOperationType_BR;
        this.VendFinalUser_BR           = vendTable.VendConsumption_BR;
        this.ServiceCodeOnDlvAddress_BR = vendTable.ServiceCodeOnDlvAddress_BR;
    }
    // </GBR>
}

