/// <summary>
///    Indicates whether any notes or attachments are present for the current record id and the table id of <c>PurchRFQTable</c> or the table id of <c>PurchRFQReplyTable</c> .
/// </summary>
/// <returns>
///    true if notes or attachments are is present; otherwise, false.
/// </returns>
public display PurchRFQDocument isAttachmentOrNotePresent()
{
    return((select  firstonly RecId
            from    docuRef
            where   docuRef.RefRecId == this.RecId &&
                    (docuRef.RefTableId == this.TableId ||
                    docuRef.RefTableId == tableNum(PurchRFQReplyTable))).RecId != 0);
}

/// <summary>
/// Determines if the current request for quotation is a new invitation.
/// </summary>
/// <returns>
/// True, if current request for quotation is a new invitation, otherwise, false;
/// </returns>
public boolean isNewInvitation()
{
    return !this.IsBidInProgress
        && this.StatusHigh == PurchRFQStatus::Sent;
}

/// <summary>
/// Checks whether the marked reply lines are valid.
/// </summary>
/// <returns>
/// true if marked reply lines are valid; otherwise, false.
/// </returns>
/// <remarks>
/// For requisition type RFQ if both original and alternate lines are marked then returning false.
/// </remarks>
public boolean isMarkedReplyLineValid()
{
    PurchRFQLine       purchRFQLine;
    PurchRFQLine       alternatePurchRFQLine;
    PurchRFQReplyLine  purchRFQReplyLine;
    PurchRFQReplyLine  alternatePurchRFQReplyLine;
    boolean            isValid = true;

    if (this.RFQType == PurchRFQType::PurchReq)
    {
        select firstonly RecId from purchRFQLine
            where purchRFQLine.RFQId == this.rfqId
                && purchRFQLine.IsAlternate == NoYes::No
        exists join purchRFQReplyLine
            where purchRFQReplyLine.RFQLineRecId == purchRFQLine.RecId
                && purchRFQReplyLine.RFQMark == NoYes::Yes
        exists join alternatePurchRFQLine
            where alternatePurchRFQLine.RFQCaseLineRefRecId == purchRFQLine.RFQCaseLineRefRecId
                && alternatePurchRFQLine.IsAlternate == NoYes::Yes
        exists join alternatePurchRFQReplyLine
            where alternatePurchRFQReplyLine.RFQLineRecId == alternatePurchRFQLine.RecId
                && alternatePurchRFQReplyLine.RFQMark == NoYes::Yes;

        if (purchRFQLine)
        {
            isValid = false;
        }
    }

    return isValid;
}

/// <summary>
/// Checks that at least one RFQ reply line has been marked.
/// </summary>
/// <returns>
/// true if at least one reply line is marked; otherwise, false.
/// </returns>
public boolean isReplyLineMarked()
{
    PurchRFQLine        purchRFQLine;
    PurchRFQReplyLine   purchRFQReplyLine;

    if (this.RFQType    == PurchRFQType::PurchReq)
    {
        select firstOnly RecId from purchRFQLine
            where purchRFQLine.RFQId == this.RFQId
        exists join purchRFQReplyLine
            where purchRFQReplyLine.RFQLineRecId == purchRFQLine.RecId
                && purchRFQReplyLine.RFQMark == NoYes::Yes;
    }

    return purchRFQLine.RecId != 0;
}

/// <summary>
/// Retrieves a record from the <c>KmVirtualNetworkAnswerTable</c> table if a questionnaire has been completed.
/// </summary>
/// <param name="_kmCollectionId">
/// Reference to a record in the <c>KMCollection</c> table.
/// </param>
/// <returns>
/// A record in the <c>KmVirtualNetworkAnswerTable</c> table.
/// </returns>
public KMVirtualNetworkAnswerTable kmVirtualNetworkAnswerTable(
    PurchRFQKMCollectionId  _kmCollectionId)
{
    PurchRFQTableKMCollection    purchRFQTableKMCollection;
    KMVirtualNetworkAnswerTable  kmVirtualNetworkAnswerTable;

    purchRFQTableKMCollection = PurchRFQTableKMCollection::findByRFQAndCollection(this.RFQId, _kmCollectionId);

    if (purchRFQTableKMCollection)
    {
        kmVirtualNetworkAnswerTable = KMVirtualNetworkAnswerTable::find(
                                          PurchRFQTableQuestionnaireResponse::findByRFQKMCollection(purchRFQTableKMCollection.RecId).KMVirtualNetworkAnswerTableId);
    }

    return kmVirtualNetworkAnswerTable;
}

/// <summary>
/// Retrieves the last sent request for quotation journal for the currect request for quotation.
/// </summary>
/// <returns>
/// The record of the <c>VendRFQJour</c> table;
/// </returns>
public VendRFQJour lastSentJournal()
{
    VendRFQJour vendRFQJour;

    select firstonly vendRFQJour
        order by vendRFQJour.CreatedDateTime desc
        where vendRFQJour.RFQId == this.RFQId
           && vendRFQJour.Status == PurchRFQStatus::Sent;

    return vendRFQJour;
}

