/// </returns>
/// <remarks>
/// This method makes sure that potential linked dimensions are not overwritten when merging.
/// Additionally, the entity specifier defaulting will be considered.
/// </remarks>

public DimensionDefault mergeDimension(
    DimensionDefault _primaryDefaultDimension,
    DimensionDefault _secondaryDefaultDimension = 0,
    DimensionMerge   _dimensionMerge = DimensionMerge::newFromTable(this,
                                                                    this.companyInfo().RecId
                                                                    )
    )
{
    return _dimensionMerge.merge(_primaryDefaultDimension, _secondaryDefaultDimension);
}

public void modifiedField(FieldId _fieldId)
{
    super(_fieldId);

    switch (_fieldId)
    {
        case fieldNum(PurchRFQTable, DeliveryPostalAddress):
            this.clearAddressReference();
            break;

        case fieldNum(PurchRFQTable, InventLocationId):
            this.InventStorageDimMap::modifiedField(fieldNum(InventStorageDimMap, InventLocationId));
            this.setAddressFromInventLocationId(this.InventLocationId);
            if (FormDataUtil::isFormDataSource(this))
            {
                FormDataUtil::getFormDataSource(this).refresh();
            }
            break;

        case fieldNum(PurchRFQTable, MarkupGroup):
            this.modifieMarkupGroup();
            break;

        case fieldNum(PurchRFQTable, InventSiteId):
            this.InventStorageDimMap::modifiedField(fieldNum(InventStorageDimMap, InventSiteId));
            this.setAddressFromInventSiteId(this.InventSiteId);
            if (FormDataUtil::isFormDataSource(this))
            {
                FormDataUtil::getFormDataSource(this).refresh();
            }
            break;
    }
}

void modifieMarkupGroup()
{
    PurchRFQLine  purchRFQLine;

    while select purchRFQLine
        index hint PurchRFQLineIdx
        where purchRFQLine.rfqId == this.rfqId
    {
        purchRFQLine.createMarkupTrans(this);
    }
}

