/// <summary>
/// Calculates the total line amount of a <c>PurchRFQTable</c> record based on the flags status.
/// </summary>
/// <returns>
/// The line amount of all lines of the <c>PurchRFQTable</c> table if the flags are checked else set the lines amount to 'Zero'.
/// </returns>
[SysClientCacheDataMethodAttribute(true)]
public display PurchLineAmount netAmount()
{
    PurchRFQCaseTable   purchRFQCaseTable;
    PurchLineAmount     purchLineAmount;

    purchRFQCaseTable = PurchRFQCaseTable::find(this.RFQCaseId);

    if (purchRFQCaseTable.showLineNetAmountEP())
    {
        purchLineAmount = (select sum(LineAmount)
                            from   purchRFQLine
                            where  purchRFQLine.rfqId == this.rfqId).LineAmount;
    }
    else
    {
        purchLineAmount = 0;
    }

    return purchLineAmount;
}

