/// <summary>
///    Creates a query that selects the <c>PurchRFQLine</c> records that are linked to the current
///    <c>PurchRFQTable</c> record.
/// </summary>
/// <param name="_update">
///    A Boolean value that indicates whether to read the record for update; optional.
/// </param>
/// <returns>
///    A query that selects the linked <c>PurchRFQLine</c> records.
/// </returns>
Query queryPurchRFQLine(boolean _update = false)
{
    DictRelation    dictRelation;
    DictField       externdictField;
    DictField       dictField;
    Query           query = new Query();
    Counter         c;

    query.addDataSource(tableNum(PurchRFQLine));

    dictRelation = new DictRelation(tableNum(PurchRFQLine));
    dictRelation.loadTableRelation(this.TableId);

    if (dictRelation.lines())
    {
        for (c=1; c <= dictRelation.lines(); c++)
        {
            externdictField   = new DictField(this.TableId,dictRelation.lineExternTableValue(c));
            dictField   = new DictField(tableNum(PurchRFQLine),dictRelation.lineTableValue(c));
            query.dataSourceTable(tableNum(PurchRFQLine)).addRange(dictField.id()).value(queryValue(this.(externdictField.id())));
        }
    }
    query.dataSourceTable(tableNum(PurchRFQLine)).addRange(fieldNum(PurchRFQLine, Status)).value(strFmt('..%1',PurchRFQStatus::Received));
    query.dataSourceTable(tableNum(PurchRFQLine)).update(_update);

    return query;
}

