boolean setAddressFromInventLocationId(InventLocationId _inventLocationId)
{
    LogisticsLocationRecId          inventLocationAddress;
    InventLocation                  inventLocation;
    boolean                         addressSet;

    addressSet = false;

    if (_inventLocationId)
    {
        inventLocation          = InventLocation::find(_inventLocationId);
        inventLocationAddress   = InventLocation::getLocationFromRole(inventLocation.RecId, LogisticsLocationRoleType::Delivery);

        if (inventLocationAddress)
        {
            this.setLocation(inventLocationAddress);
            addressSet = true;
        }
    }

    if (!addressSet)
    {
        addressSet = this.setAddressFromInventSiteId(this.InventSiteId);
    }

    return addressSet;
}

boolean setAddressFromInventSiteId(InventSiteId _inventSiteId)
{
    LogisticsLocationEntity location;
    InventSite              inventSite;
    boolean                 addressSet = false;

    if (_inventSiteId)
    {
        inventSite  = InventSite::find(_inventSiteId);
        location    = LogisticsLocationEntity::findLocation(inventSite, LogisticsLocationRoleType::Delivery);

        if (location && location.parmLocationRecId())
        {
            this.setLocation(location.parmLocationRecId());
            addressSet = true;
        }
    }

    if (!addressSet)
    {
        this.initDefaultAddress();
        addressSet = true;
    }

    return addressSet;
}

void  setLocation(LogisticsLocationRecId _locationRecId)
{
    //LogisticsLocationEntity location = LogisticsLocationEntity::constructFromLocationRecId(_location);
    LogisticsLocation location;

    this.DeliveryPostalAddress = LogisticsPostalAddress::addressRecIdFromLocationRecId(_locationRecId);
    if (_locationRecId)
    {
        location = LogisticsLocation::find(_locationRecId);
        if (location.Description)
        {
            this.DeliveryName = location.Description;
        }
        this.AddressRefRecId            = _locationRecId;
        this.AddressRefTableId          = tableNum(LogisticsLocation);
    }
}

