public void update()
{
    if (appl.ttsLevel() == 0)
    {
        throw error(strFmt("@SCM:TransactionExpected", funcName()));
    }

    this.updateStatus(true, false);

    if (this.MarkupGroup  != this.orig().MarkupGroup)
    {
        this.createMarkupTrans();
    }

    super();

    if (TaxSolutionScopeIntegrationUtil::IsCompanyEnabled())
    {
        TaxDocumentLinePurchRFQUtil::markCurrentTaxDocumentTaxStatus(this, TaxDocumentCalculationLevel::Messy);
    }
}

