void updateStatus(boolean _skipUpdate = false, boolean _headerUpdated = false)
{
    PurchRFQCaseTable   purchRFQCaseTable;
    PurchRFQStatus      statusHigh;
    PurchRFQStatus      statusLow;

    //it does not make sence to update status if record does not exist.
    if (this.RecId)
    {
        ttsbegin;
        statusHigh = PurchRFQLine::highestStatus(this.rfqId);
        statusLow  = PurchRFQLine::lowestStatus(this.rfqId);
        if (this.StatusHigh != statusHigh ||
            this.StatusLow  != statusLow)
        {
            this.StatusHigh = statusHigh;
            this.StatusLow  = statusLow;
        }

        if (_headerUpdated &&
            this.StatusHigh < PurchRFQStatus::Received)
        {//Header is updated and no lines have been updated.
            this.StatusHigh = PurchRFQStatus::Received;
        }

        boolean updateRFQCaseStatus = PurchRFQUpdateCaseStatusAfterRFQStatusUpdateFlight::instance().isEnabled();

        purchRFQCaseTable = PurchRFQCaseTable::find(this.rfqCaseId, true);
        if (!updateRFQCaseStatus && purchRFQCaseTable)
        {
            unchecked(Uncheck::XDS)
            {
                purchRFQCaseTable.updateStatus();
            }
        }

        if (!_skipUpdate)
        {
            this.doUpdate();
        }

        if (updateRFQCaseStatus && purchRFQCaseTable)
        {
            unchecked(Uncheck::XDS)
            {
                purchRFQCaseTable.updateStatus();
            }
        }

        ttscommit;
    }
}

public boolean validateDelete()
{
    boolean ret = true;

    ret = super();

    if (ret && (this.StatusHigh < PurchRFQStatus::Received))
    {
        if (PurchRFQLine::exist(this.rfqId))
        {
            if (Box::yesNo("@SYS105324", DialogButton::No) == DialogButton::No)
            {
                ret = checkFailed("@SYS19437");
            }
        }
    }
    else
    {
        ret = checkFailed(strFmt("@SYS105325",this.StatusHigh));
    }

    return ret;
}

/// <summary>
/// Converts the RFQ high status value from the employee view to the vendor view.
/// </summary>
/// <returns>
/// The converted enumeration value, as applicable for the vendor.
/// </returns>
/// <remarks>
/// When all lines have the same status and all are in Sent status, the header status will be New:
/// Action required.When all lines have the same status and lines are in any other status, the header
/// status will be same as the line status with No Action Required added to it.When lines are all in
/// different statuses and there is at least one line in Sent status, the header status will be
/// Multiple: Action required.When lines are all in different statuses and there are no lines in Sent
/// status, the header status will be Multiple: No action required.
/// </remarks>
public display PurchRFQVendStatus vendorStatus()
{
    DictEnum            dictEnum = new DictEnum(enumNum(PurchRFQStatusVendor));
    PurchRFQStatus      statusHigh;
    PurchRFQStatus      statusLow;
    PurchRFQVendStatus  rfqStatusDisplay;
    boolean             actionRequired;
    statusHigh = PurchRFQLine::highestStatus(this.rfqId);
    statusLow  = PurchRFQLine::lowestStatus(this.rfqId);

    actionRequired = statusLow == PurchRFQStatus::Sent;
    if (this.StatusHigh > statusHigh)
    {
        statusHigh =  this.StatusHigh;
    }

    if (this.StatusLow < statusLow)
    {
        statusLow =  this.StatusLow;
    }

    if (statusHigh == statusLow)
    {
        rfqStatusDisplay = dictEnum.index2Name(enum2int(this.StatusHigh));
    }
    else
    {
        rfqStatusDisplay = "@SYS78137";
    }

    if (actionRequired)
    {
        if (this.ExpiryDateTime < DateTimeUtil::utcNow())
        {
            rfqStatusDisplay = "@SYS107504";
        }
        else
        {
            rfqStatusDisplay += "@SYS344477";
        }
    }
    else
    {
        rfqStatusDisplay += "@SYS344478";
    }

    return rfqStatusDisplay;
}

