static boolean exist(PurchRFQId rfqId)
{
    return (select firstonly RecId from purchRFQTable
                index hint RFQIdx
                where purchRFQTable.rfqId    == rfqId).RecId != 0;
}

public static PurchRFQTable find(
    PurchRFQId _rfqId,
    boolean    _forUpdate = false)

{
    PurchRFQTable  purchRFQTable;

    if (_rfqId)
    {
        purchRFQTable.selectForUpdate  (_forUpdate);

        select firstonly purchRFQTable
            index hint RFQIdx
            where purchRFQTable.rfqId == _rfqId;
    }

    return purchRFQTable;
}

/// <summary>
/// Finds the <c>PurchRFQCaseTable</c> record.
/// </summary>
/// <param name="_rfqId">
/// The RFQ ID of a <c>PurchRFQTable</c> record.
/// </param>
/// <param name="_forUpdate">
/// A Boolean value that indicates whether the record will be updated.
/// </param>
/// <returns>
/// The <c>PurchRFQCaseTable</c> record.
/// </returns>
public static PurchRFQCaseTable findByRFQId(PurchRFQId   _rfqId, boolean _forUpdate = false)
{
    PurchRFQTable       purchRFQTable;
    PurchRFQCaseTable   purchRFQCaseTable;

    if (_rfqId)
    {
        purchRFQCaseTable.selectForUpdate(_forUpdate);

        select firstOnly purchRFQCaseTable
            exists join purchRFQTable
            where purchRFQCaseTable.RFQCaseId   == purchRFQTable.RFQCaseId
                && purchRFQTable.RFQId          == _rfqId;
    }

    return purchRFQCaseTable;
}

/// <summary>
///    Finds the record that is associated with the specified record ID.
/// </summary>
/// <param name="_recId">
///    The record ID of a <c>PurchRFQTable</c> record.
/// </param>
/// <param name="_forUpdate">
///    A Boolean value that indicates whether the record will be updated.
/// </param>
/// <returns>
///    The <c>PurchRFQTable</c> record that is found by the record ID.
/// </returns>
public static PurchRFQTable findRecId(RecId   _recId, boolean _forUpdate = false)
{
    PurchRFQTable    purchRFQTable;

    if (_recId)
    {
        purchRFQTable.selectForUpdate(_forUpdate);

        select firstonly purchRFQTable
            where purchRFQTable.RecId  == _recId;
    }

    return purchRFQTable;
}

static PurchRFQTable findRFQCaseId(PurchRFQCaseId _rfqCaseId,
                                   VendAccount    _vendAccount  = '',
                                   boolean        _forUpdate    = false)

{
    PurchRFQTable  purchRFQTable;

    if (_rfqCaseId)
    {
        purchRFQTable.selectForUpdate  (_forUpdate);

        select firstonly purchRFQTable
            where purchRFQTable.rfqCaseId == _rfqCaseId
               && (_vendAccount == '' || purchRFQTable.VendAccount == _vendAccount);
    }

    return purchRFQTable;
}

/// <summary>
/// Updates the CFOP of the request for quotation lines with the default value.
/// </summary>
/// <param name="_purchRFQTableRecId">
/// The request for quotation recId.
/// </param>
static void  updateCFOPcodeInLines_BR(RefRecId _purchRFQTableRecId)
{
    PurchRFQLine  purchRFQLine;
    PurchRFQId    rfqId;

    rfqId = PurchRFQTable::findRecId(_purchRFQTableRecId).RFQId;

    ttsbegin;

    while select forupdate purchRFQLine
        where purchRFQLine.rfqId == rfqId
    {
        purchRFQLine.CFOPTable_BR = CFOPValidation_BR::construct(purchRFQLine).getDefaultCFOP();

        purchRFQLine.update();
    }

    ttscommit;
}

