/// <summary>
/// Gets the reply status based on the RFQ case status value.
/// </summary>
/// <param name = "_statusLow">
/// The status value of the RFQ case.
/// </param>
/// <returns>
/// The reply status for RFQ case.
/// </returns>
public static PurchRFQReplyStatus_PSN getReplyStatusByRFQStatus(PurchRFQStatus _statusLow)
{
    return (_statusLow == PurchRFQStatus::Received || _statusLow == PurchRFQStatus::Rejected || _statusLow == PurchRFQStatus::Accepted) ? PurchRFQReplyStatus_PSN::Complete : PurchRFQReplyStatus_PSN::Partial;
}

/// <summary>
/// Gets the awarded status for RFQ case based on the status value.
/// </summary>
/// <param name = "_statusHigh">
/// The status value of the RFQ case.
/// </param>
/// <returns>
/// true if the status value is accepted; otherwise, false.
/// </returns>
public static IsAwarded_PSN getAwardedByRFQStatus(PurchRFQStatus _statusHigh)
{
    return _statusHigh == PurchRFQStatus::Accepted ? NoYes::Yes: NoYes::No;
}
