/// <summary>
/// Checks employee before calculatations.
/// </summary>
/// <param name="_dateCalc">
/// The date to perform calculation on.
/// </param>
/// <param name="_errorMessage">
/// The error message should be shown if yes.
/// </param>
/// <param name="_ignoreHiringCheck">
/// The hiring checks will be ignored if true.
/// </param>
/// <param name="_checkDismissal">
/// The employee is dismissed check will be performed if true.
/// </param>
/// <returns>
/// False if empoloyee checks found errors, otherwise true.
/// </returns>
[Microsoft.Dynamics.BusinessPlatform.SharedTypes.InternalUseOnlyAttribute]
public boolean checkEmplBeforeCalc(ReportPeriod_RU _dateCalc,
                                      NoYes        _errorMessage      = NoYes::No,
                                      boolean      _ignoreHiringCheck = false,
                                      boolean      _checkDismissal    = true)
{
    if (this.PayBlocked && ! this.PayBlockedFrom)
    {
        if (_errorMessage)
            error(strFmt("@RUP134", this.EmployeeId));
        return false;
    }

    if (this.PayBlockedFrom <= _dateCalc && this.PayBlocked)
    {
        if (_errorMessage)
            error(strFmt("@RUP135", this.EmployeeId, this.PayBlockedFrom));
        return false;
    }

    if (! _ignoreHiringCheck)
    {
        if (! this.PayEmploymentDate)
        {
            if (_errorMessage)
                error(strFmt("@RUP136",this.EmployeeId));
            return false;
        }

        if (this.PayEmploymentDate > _dateCalc)
        {
            if (_errorMessage)
                error(strFmt("@RUP137", this.EmployeeId, this.PayEmploymentDate));
            return false;
        }

        if (_checkDismissal && endmth(this.PayResignedDate) < _dateCalc && (this.PayResignedDate))
        {
            if (_errorMessage)
                error(strFmt("@RUP138", this.EmployeeId, this.PayResignedDate));
            return false;
        }
    }
    return true;
}

