}

public void delete()
{
    RHRMEmplTable    emplTableLocal;
    EmployeeTable_RU employeeTable, employeeTableSearch;
    RHRMPersonTable  personTable;
    boolean          isLastEmployee;
    RPayGroupTable   groupTable;
    RPayGroupMember  groupMember;

    select count(RecId) from emplTableLocal where emplTableLocal.rhrmPersonTable == this.rhrmPersonTable;

    isLastEmployee = emplTableLocal.RecId == 1;

    employeeTable = this.employeeTable();

    ttsbegin;

    delete_from groupMember
        where groupMember.PersonEmplId == this.EmployeeId
        exists join groupTable
            where groupMember.PayGroup == groupTable.PayGroup
               && groupTable.Type == RPayGroupType::Employee;

    if ( !this.validateDelete())
    {
        throw Exception::Error;
    }

    super();

    if (isLastEmployee)
    {
        personTable = this.personTable(true);
        if (personTable.validateDelete())
        {
            personTable.delete();
        }
        else
        {
            throw Exception::Error;
        }
    }

    select firstonly RecId from employeeTableSearch
        where employeeTableSearch.HcmEmployment == employeeTable.HcmEmployment
           && employeeTableSearch.RecId         != employeeTable.RecId;

    if (employeeTableSearch.RecId)
    {
        this.employeeTable(true).delete();
    }
    ttscommit;
}

