/// <summary>
/// Gets or sets <c>RPayParmContent</c> value in <c>RPayParmEmplTrans.Contents</c> table.
/// </summary>
/// <param name="_set">
/// A set flag, if true value will be set.
/// </param>
/// <param name="_val">
/// A <c>RPayParmContent</c> array value.
/// </param>
/// <param name="_fieldDate">
/// A rate code.
/// </param>
/// <returns>
/// The <c>RPayParmContent</c> array.
/// </returns>
private RPayParmContent editPayParmDate(boolean _set, RPayParmContent _val, RPayFieldDate _fieldDate)
{
    RPayParmEmplTrans rPayParmEmplTrans;

    if (_set)
    {
        ttsbegin;
        rPayParmEmplTrans = RPayParmEmplTrans::find(this.EmployeeId, _fieldDate, true);
        if (! rPayParmEmplTrans)
        {
            rPayParmEmplTrans.EmplId   = this.EmployeeId;
            rPayParmEmplTrans.PayField = _fieldDate;

            rPayParmEmplTrans.insert();
        }

        rPayParmEmplTrans.Contents = _val;

        if (rPayParmEmplTrans.validateWrite())
        {
            rPayParmEmplTrans.write();
        }
        ttscommit;
    }
    else
    {
        rPayParmEmplTrans = RPayParmEmplTrans::find(this.EmployeeId, _fieldDate);
    }

    return rPayParmEmplTrans.Contents;
}

