/// <summary>
/// Gets employment status.
/// </summary>
/// <returns>
/// The employement status of employee.
/// </returns>
[Microsoft.Dynamics.BusinessPlatform.SharedTypes.InternalUseOnlyAttribute]
display public RHRMEmploymentStatus employmentStatus()
{
    RHRMEmploymentStatus rhrmEmploymentStatus;

    if (!RPayHistory::exist(this.EmployeeId))
    {
        rhrmEmploymentStatus = RHRMEmploymentStatus::None;
    }
    else if (RPayHistory::existNetworkLastRetired(this.EmployeeId))
    {
        rhrmEmploymentStatus = RHRMEmploymentStatus::Resigned;
    }
    else
    {
        rhrmEmploymentStatus = RHRMEmploymentStatus::Employed;
    }

    return rhrmEmploymentStatus;
}

/// <summary>
/// Gets employment status on date.
/// </summary>
/// <param name="_date">
/// Date for processing.
/// </param>
/// <returns>
/// Employment status.
/// </returns>
[Microsoft.Dynamics.BusinessPlatform.SharedTypes.InternalUseOnlyAttribute]
public RHRMEmploymentStatus employmentStatusOnDate(TransDate _date)
{
    if (!RPayHistory::exist(this.EmployeeId))
    {
        return RHRMEmploymentStatus::None;
    }

    if (RHRMJournalType::Dismiss == RPayHistory::getHistoryLineByDate(this.EmployeeId, _date, true)
                                                    .operationType)
    {
        if (RHRMJournalType::Dismiss == RPayHistory::getHistoryLineByDate(
                                            this.EmployeeId,
                                            _date - 1,
                                            true).operationType)
        {
        return RHRMEmploymentStatus::Resigned;
    }
    }

    return RHRMEmploymentStatus::Employed;
}

