/// <summary>
/// Initializes rates from parameters.
/// </summary>
[Microsoft.Dynamics.BusinessPlatform.SharedTypes.InternalUseOnlyAttribute]
public void initRates()
{
    RHRMParameters rhrmParameters = RHRMParameters::find();

    if (!this.EmploymentFactorRate)
    {
        this.EmploymentFactorRate = rhrmParameters.EmploymentFactorRate;
    }

    if (!this.WageRate)
    {
        this.WageRate = rhrmParameters.WageRate;
    }

    if (!this.RiseInWages1Rate)
    {
        this.RiseInWages1Rate = rhrmParameters.RiseInWages1Rate;
    }

    if (!this.RiseInWages2Rate)
    {
        this.RiseInWages2Rate = rhrmParameters.RiseInWages2Rate;
    }

    if (!this.RiseInWages3Rate)
    {
        this.RiseInWages3Rate = rhrmParameters.RiseInWages3Rate;
    }
}

