{
    RHRMPersonTable personTable;

    ttsbegin;
    super();

    if (!this.personTable().PayMainEmplId)
    {
        personTable = this.personTable(true);
        personTable.PayMainEmplId = this.EmployeeId;
        personTable.doUpdate();
    }

    RPayGroupMemberEngine::updatePayGroup(this);
    ttscommit;
}

/// <summary>
/// Performs worker lookup on the form.
/// </summary>
/// <param name="_ctrl">
/// The form where lookup should be performed.
/// </param>
[Microsoft.Dynamics.BusinessPlatform.SharedTypes.InternalUseOnlyAttribute]
public void lookupByWorker(Object _ctrl)
{
    QueryBuildDataSource    queryBuildDataSource;
    QueryBuildRange         queryBuildRange;
    Args                    args;
    FormRun                 formRun;
    FormDataSource          ds;

    args = new Args();
    args.name(formStr(RHRMEmployeeLookup));
    args.caller(_ctrl);

    formRun = classfactory.formRunClass(args);
    formRun.init();

    ds = formRun.dataSource();
    queryBuildDataSource = ds.query().dataSourceTable(tableNum(RHRMEmplTable));
    queryBuildRange      = queryBuildDataSource.addRange(fieldNum(RHRMEmplTable, rhrmPersonTable));
    queryBuildRange.value(queryValue(this.rhrmPersonTable));

    _ctrl.performFormLookup(formRun);
}

