[Microsoft.Dynamics.BusinessPlatform.SharedTypes.InternalUseOnlyAttribute]
public boolean rateHasUsed(RHRMRateEmployee _hrmRateEmployee)
{
    return this.EmploymentFactorRate == _hrmRateEmployee ||
           this.WageRate             == _hrmRateEmployee ||
           this.RiseInWages1Rate     == _hrmRateEmployee ||
           this.RiseInWages2Rate     == _hrmRateEmployee ||
           this.RiseInWages3Rate     == _hrmRateEmployee ||
           RHRMParameters::setOfChildrenNumberRate().in(_hrmRateEmployee) ||
           RHRMParameters::find().WorkCondClassRate == _hrmRateEmployee;
}

