/// <summary>
/// Synchronizes identity documents between <c>EmployeeTable_RU</c> and <c>RHRMPersonTable</c>
/// </summary>
[Microsoft.Dynamics.BusinessPlatform.SharedTypes.InternalUseOnlyAttribute]
public void syncIdentityDocument()
{
    EmployeeTable_RU    employeeTable;
    RHRMPersonTable     personTable = this.personTable();

    if (personTable.IdentityCard)
    {
        employeeTable = this.employeeTable(true);

        if (employeeTable.RecId && !employeeTable.IdentityCard && !employeeTable.IdentityCard)
        {
            employeeTable.initFromHRMPerson(personTable);
            employeeTable.update();
        }
    }
}

