public void update()
{
    RHRMEmplTable rhrmEmplTableOrig = this.orig();

    ttsbegin;
    super();
    RPayGroupMemberEngine::updatePayGroup(this, rhrmEmplTableOrig);
    ttscommit;
}

public boolean validateField(FieldId _fieldIdToCheck)
{
    RPayTrans   payTrans;
    boolean ret;

    ret = super(_fieldIdToCheck);

    if (ret)
    {
        switch (_fieldIdToCheck)
        {
            case fieldNum(RHRMEmplTable, PayBlockedFrom):
                if (this.PayBlocked && this.PayBlockedFrom)
                {
                    select firstonly RecId from payTrans
                        where  payTrans.Emplid == this.EmployeeId
                            && payTrans.PayPeriod > this.PayBlockedFrom;
                    if (payTrans.RecId)
                    {
                        checkFailed("@RUP6337");
                    }
                }
                break;
            case fieldNum(RHRMEmplTable, PayBlocked):
                if (this.PayBlocked == NoYes::No && this.personTable().TaxPayBlocked == NoYes::Yes)
                {
                    ret = checkFailed("@RUP6338");
                }
                break;
        }
    }

    return ret;
}

