/// <summary>
/// Determines if record by employee id exists.
/// </summary>
/// <param name="_emplId">
/// An employee id.
/// </param>
/// <returns>
/// True if record exists.
/// </returns>
[Microsoft.Dynamics.BusinessPlatform.SharedTypes.InternalUseOnlyAttribute]
public static boolean exist(RHRMEmplId _emplId)
{
    RHRMEmplTable       emplTable;

    if (_emplId)
    {
        select firstonly RecId from emplTable
            where emplTable.EmployeeId == _emplId;
    }

    return emplTable.RecId != 0;
}

