    return emplTable;
}

/// <summary>
/// Gets the worker default dimension.
/// </summary>
/// <param name="_emplId">
/// The employee id.
/// </param>
/// <returns>
/// The default worker dimension.
/// </returns>
[Microsoft.Dynamics.BusinessPlatform.SharedTypes.InternalUseOnlyAttribute]
public static DimensionDefault getWorkerDefaultDimension(RHRMEmplId _emplId)
{
    DimensionAttributeValueSetStorage dimAttrValueSetStorage;
    DimensionAttributeValue           dimAttrValue;
    DimensionDefault                  defaultDimension;
    RHRMPersonTable                   personTable;
    HcmWorker                         worker;
    RHRMEmplTable                     emplTable;
    DimensionAttributeRecId           attributeId = RPayParameters::getEmplDimensionAttribute();

    if (attributeId)
    {
        select firstonly RecId from worker
            exists join personTable
                where personTable.PersonnelNumber == worker.PersonnelNumber
            exists join emplTable
                where emplTable.rhrmPersonTable == personTable.RecId
                    && emplTable.EmployeeId      == _emplId;

        if (worker.RecId )
        {
            dimAttrValue = DimensionAttributeValue::findByDimensionAttributeAndEntityInst(
                attributeId,
                worker.RecId,
                false,
                true);

            if (dimAttrValue.RecId)
            {
                dimAttrValueSetStorage = DimensionAttributeValueSetStorage::find(defaultDimension);
                dimAttrValueSetStorage.addItem(dimAttrValue);
                defaultDimension = dimAttrValueSetStorage.save();
            }
        }
    }

    return defaultDimension;
}

/// <summary>
///     Fills the segment in the default dimension.
/// </summary>
/// <param name="_emplId">
///    The ID of the employee.
/// </param>
/// <param name="_ledgerDimension">
///     The ledger dimension. This value is used for obtaining of account structure.
/// </param>
/// <returns>
///     The default dimension that contains worker value.
/// </returns>
[Microsoft.Dynamics.BusinessPlatform.SharedTypes.InternalUseOnlyAttribute]
public static DimensionDefault getWorkerDimension(RHRMEmplId _emplId, LedgerDimensionAccount _ledgerDimension = 0)
{
    DimensionAttributeValueSetStorage dimAttrValueSetStorage;
    DimensionAttributeValue           dimAttrValue;
    LedgerDimensionAccount            defaultDimension;
    RHRMPersonTable                   personTable;
    DimensionHierarchyId              accStructure;
    DimensionHierarchyLevel           dimensionHierarchyLevel;
    DimensionAttribute                dimAttribute;
    HcmWorker                         worker;
    RHRMEmplTable                     emplTable;
    DimensionAttributeRecId           attributeId = RPayParameters::getEmplDimensionAttribute();

    if (!_emplId)
    {
        return 0;
    }

    if (prmisDefault(_ledgerDimension))
    {
        attributeId = RPayParameters::getEmplDimensionAttribute();
    }
    else
    {
        accStructure = DimensionHierarchy::getAccountStructure(MainAccount::findByLedgerDimension(_ledgerDimension).RecId);

        select DimensionAttribute from dimensionHierarchyLevel
            where dimensionHierarchyLevel.DimensionHierarchy == accStructure
            exists join dimAttribute
                where dimAttribute.RecId == dimensionHierarchyLevel.DimensionAttribute
                   && dimAttribute.BackingEntityType == tableNum(DimAttributeHcmWorker);

        attributeId = dimensionHierarchyLevel.DimensionAttribute;
    }

    if (attributeId)
    {
        select firstonly RecId from worker
            exists join personTable
                where personTable.PersonnelNumber == worker.PersonnelNumber
            exists join emplTable
                where emplTable.rhrmPersonTable == personTable.RecId
                   && emplTable.EmployeeId      == _emplId;

        if (worker.RecId)
        {
            dimAttrValue = DimensionAttributeValue::findByDimensionAttributeAndEntityInst(
                attributeId,
                worker.RecId,
                false,
                true);

            if (dimAttrValue.RecId)
            {
                dimAttrValueSetStorage = DimensionAttributeValueSetStorage::find(defaultDimension);
                dimAttrValueSetStorage.addItem(dimAttrValue);
                defaultDimension = dimAttrValueSetStorage.save();
            }
        }
    }

    return defaultDimension;
}
