/// <summary>
/// Checks the identity card.
/// </summary>
/// <param name="_birthDate">
/// The birth date.
/// </param>
/// <returns>
/// True if identity card if valid, otherwise false.
/// </returns>
/// <remarks>
/// This method as facade to engine based on advance holders table.
/// </remarks>
[Microsoft.Dynamics.BusinessPlatform.SharedTypes.InternalUseOnlyAttribute]
public boolean checkIdentityCard(DirPersonBirthDate _birthDate)
{
    EmployeeTable_RU employeeTable;
    HcmEmployment    employment;
    boolean          ret = true;

    if (this.IdentityCard)
    {
        employment = HcmEmployment::findByWorkerLegalEntity(this.hcmWorker().RecId, CompanyInfo::current());

        employeeTable.IdentityCard          = this.IdentityCard;
        employeeTable.IdentityCardIssueBy   = this.IdentityCardIssueBy;
        employeeTable.IdentityCardIssueDate = this.IdentityCardIssueDate;
        employeeTable.IdentityCardNumber    = this.IdentityCardNumber;
        employeeTable.IdentityCardSeries    = this.IdentityCardSeries;
        employeeTable.HcmEmployment         = employment.RecId;

        ret = RHRMIdentityCardEngine::checkEmployeeTable(employeeTable, _birthDate);
    }

    return ret;
}

