public void delete()
{
    RPayGroupMember groupMember;
    RPayGroupTable  groupTable;

    if ( !this.validateDelete())
    {
        throw Exception::Error;
    }

    delete_from groupMember
        where groupMember.PersonEmplId == this.PersonnelNumber
        exists join groupTable
            where groupMember.PayGroup == groupTable.PayGroup
               && groupTable.Type == RPayGroupType::Person;

    super();
}

/// <summary>
/// The person name.
/// </summary>
/// <returns>
/// The name.
/// </returns>
[Microsoft.Dynamics.BusinessPlatform.SharedTypes.InternalUseOnlyAttribute]
public DirPersonName dirPersonName()
{
    HcmWorker       worker;
    DirPersonName   dirPersonName;

    select firstonly * from dirPersonName
        exists join worker
            where worker.PersonnelNumber == this.PersonnelNumber &&
                  worker.Person          == dirPersonName.Person;

    return dirPersonName;
}

/// <summary>
/// The first, middle, last name as a string.
/// </summary>
/// <param name="shorten">
/// The indicator, if true the result is shorten.
/// </param>
/// <returns>
/// The name.
/// </returns>
#define.dot(".")
#define.space(" ")
public EmplName fio(boolean shorten = true)
{
    EmplName ret;
    DirPersonName dirPersonName = this.dirPersonName();

    ret = dirPersonName.LastName;

    if (dirPersonName.FirstName)
    {
        if (shorten)
            ret += #space + subStr(dirPersonName.FirstName, 1, 1) + #dot;
        else
            ret += #space + dirPersonName.FirstName;
    }

    if (dirPersonName.MiddleName)
    {
        if (shorten)
            ret += #space + subStr(dirPersonName.MiddleName, 1, 1) + #dot;
        else
            ret += #space + dirPersonName.MiddleName;
    }

    return ret;
}

