/// <returns>
/// A postal address.
/// </returns>
[Microsoft.Dynamics.BusinessPlatform.SharedTypes.InternalUseOnlyAttribute]
public LogisticsPostalAddress getPostalAddressByRole(
    LogisticsLocationRoleType   _role = LogisticsLocationRoleType::RealAddress_RU,
    TransDateTime               _transDateTime = DateTimeUtil::utcNow())
{
    LogisticsLocationEntity logisticsLocationEntity;

    logisticsLocationEntity = LogisticsLocationEntity::constructFromLocationRecId(
        DirPartyTable::getLocationFromRole(this.hcmWorker().Person, _role),
        false,
        _transDateTime);

    return logisticsLocationEntity.getPostalAddress();
}

