private void syncEmpl()
{
    RHRMEmplTable emplTable;
    boolean       needWrite;

    while select forupdate emplTable
        where emplTable.rhrmPersonTable == this.RecId
    {
        needWrite = false;
        if (this.IdentityCard)
        {
            emplTable.syncIdentityDocument();
        }

        if (needWrite)
        {
            emplTable.write();
        }
    }
}

public void update()
{
    RHRMPersonTable rhrmPersonTableOrig = this.orig();

    ttsbegin;
    super();
    RPayGroupMemberEngine::updatePayGroup(this, rhrmPersonTableOrig);
    this.syncEmpl();
    ttscommit;
}

public boolean validateField(FieldId _fieldIdToCheck)
{
    RHRMEmplTable emplTable;
    StandardBirthPlaceCheck_RU standardBirthPlaceCheck;
    boolean ret;

    ret = super(_fieldIdToCheck);

    switch (_fieldIdToCheck)
    {
        case fieldNum(RHRMPersonTable, StateBirth):
            standardBirthPlaceCheck = new StandardBirthPlaceCheck_RU(this);
            ret = standardBirthPlaceCheck.checkStateBirth();
            break;

        case fieldNum(RHRMPersonTable, CountyBirth):
            standardBirthPlaceCheck = new StandardBirthPlaceCheck_RU(this);
            ret = standardBirthPlaceCheck.checkCountyBirth();
            break;

        case fieldNum(RHRMPersonTable, TownBirth):
            standardBirthPlaceCheck = new StandardBirthPlaceCheck_RU(this);
            ret = standardBirthPlaceCheck.checkTownBirth();
            break;

        case fieldNum(RHRMPersonTable, TaxPayBlocked):
            if (this.TaxPayBlocked)
            {
                while select EmployeeId from  emplTable
                    where  emplTable.rhrmPersonTable == this.RecId
                        && emplTable.PayBlocked      == NoYes::No
                {
                    ret = checkFailed(strFmt("@RUP6336", emplTable.EmployeeId));
                }
            }
            break;
    }

    return ret;
}

public boolean validateWrite()
{
    StandardBirthPlaceCheck_RU standardBirthPlaceCheck;
    boolean ret;

    ret = super();

    if (ret)
    {
        standardBirthPlaceCheck = new StandardBirthPlaceCheck_RU(this);
        ret = standardBirthPlaceCheck.checkBirthPlace();
    }

    return ret;
}

/// <summary>
/// Finds a record.
/// </summary>
/// <param name="_personnelId">
/// A personnel id.
/// </param>
/// <param name="_forUpdate">
/// An indicator, if true record will be selected for update.
/// </param>
/// <returns>
/// A <c>RHRMPersonTable</c> table record.
/// </returns>
[Microsoft.Dynamics.BusinessPlatform.SharedTypes.InternalUseOnlyAttribute]
public static RHRMPersonTable find(RHRMPersonnelNumberId _personnelId, boolean _forUpdate = false)
{
    RHRMPersonTable personTable;

    personTable.selectForUpdate(_forUpdate);

    if (_personnelId)
    {
        select firstonly personTable
            where personTable.PersonnelNumber == _personnelId;
    }

    return personTable;
}

